namespace RonSijm.FluidHumidifier.Factories.ACMPCA;

public class CertificateAuthorityFactory(string resourceName = null, Action<Humidifier.ACMPCA.CertificateAuthority> factoryAction = null) : ResourceFactory<Humidifier.ACMPCA.CertificateAuthority>(resourceName)
{

    internal InnerCertificateAuthorityCsrExtensionsFactory CsrExtensionsFactory { get; set; }

    internal InnerCertificateAuthorityRevocationConfigurationFactory RevocationConfigurationFactory { get; set; }

    internal InnerCertificateAuthoritySubjectFactory SubjectFactory { get; set; }

    protected override Humidifier.ACMPCA.CertificateAuthority Create()
    {
        var certificateAuthorityResult = CreateCertificateAuthority();
        factoryAction?.Invoke(certificateAuthorityResult);

        return certificateAuthorityResult;
    }

    private Humidifier.ACMPCA.CertificateAuthority CreateCertificateAuthority()
    {
        var certificateAuthorityResult = new Humidifier.ACMPCA.CertificateAuthority
        {
            GivenName = InputResourceName,
        };

        return certificateAuthorityResult;
    }
    public override void CreateChildren(Humidifier.ACMPCA.CertificateAuthority result)
    {
        base.CreateChildren(result);

        result.CsrExtensions ??= CsrExtensionsFactory?.Build();
        result.RevocationConfiguration ??= RevocationConfigurationFactory?.Build();
        result.Subject ??= SubjectFactory?.Build();
    }

} // End Of Class

