namespace RonSijm.FluidHumidifier.Factories.Config;

public class ConfigurationAggregatorFactory(string resourceName = null, Action<Humidifier.Config.ConfigurationAggregator> factoryAction = null) : ResourceFactory<Humidifier.Config.ConfigurationAggregator>(resourceName)
{

    internal List<InnerConfigurationAggregatorAccountAggregationSourceFactory> AccountAggregationSourcesFactories { get; set; } = [];

    internal InnerConfigurationAggregatorOrganizationAggregationSourceFactory OrganizationAggregationSourceFactory { get; set; }

    protected override Humidifier.Config.ConfigurationAggregator Create()
    {
        var configurationAggregatorResult = CreateConfigurationAggregator();
        factoryAction?.Invoke(configurationAggregatorResult);

        return configurationAggregatorResult;
    }

    private Humidifier.Config.ConfigurationAggregator CreateConfigurationAggregator()
    {
        var configurationAggregatorResult = new Humidifier.Config.ConfigurationAggregator
        {
            GivenName = InputResourceName,
        };

        return configurationAggregatorResult;
    }
    public override void CreateChildren(Humidifier.Config.ConfigurationAggregator result)
    {
        base.CreateChildren(result);

        result.AccountAggregationSources = AccountAggregationSourcesFactories.Any() ? AccountAggregationSourcesFactories.Select(x => x.Build()).ToList() : null;
        result.OrganizationAggregationSource ??= OrganizationAggregationSourceFactory?.Build();
    }

} // End Of Class

