namespace RonSijm.FluidHumidifier.Factories.NimbleStudio;

public class StudioComponentFactory(string resourceName = null, Action<Humidifier.NimbleStudio.StudioComponent> factoryAction = null) : ResourceFactory<Humidifier.NimbleStudio.StudioComponent>(resourceName)
{

    internal List<InnerStudioComponentStudioComponentInitializationScriptFactory> InitializationScriptsFactories { get; set; } = [];

    internal List<InnerStudioComponentScriptParameterKeyValueFactory> ScriptParametersFactories { get; set; } = [];

    internal InnerStudioComponentStudioComponentConfigurationFactory ConfigurationFactory { get; set; }

    protected override Humidifier.NimbleStudio.StudioComponent Create()
    {
        var studioComponentResult = CreateStudioComponent();
        factoryAction?.Invoke(studioComponentResult);

        return studioComponentResult;
    }

    private Humidifier.NimbleStudio.StudioComponent CreateStudioComponent()
    {
        var studioComponentResult = new Humidifier.NimbleStudio.StudioComponent
        {
            GivenName = InputResourceName,
        };

        return studioComponentResult;
    }
    public override void CreateChildren(Humidifier.NimbleStudio.StudioComponent result)
    {
        base.CreateChildren(result);

        result.InitializationScripts = InitializationScriptsFactories.Any() ? InitializationScriptsFactories.Select(x => x.Build()).ToList() : null;
        result.ScriptParameters = ScriptParametersFactories.Any() ? ScriptParametersFactories.Select(x => x.Build()).ToList() : null;
        result.Configuration ??= ConfigurationFactory?.Build();
    }

} // End Of Class

